import React from 'react';
import logo from './logo.svg';
import './App.css';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Container from '@material-ui/core/Container';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Input from '@material-ui/core/Input';

function App() {
  const [value, setValue] = React.useState(['female']);

  const handleChange = (index) => (event) => {
    let valueCopy = [...value]
    valueCopy[index] = event.target.value;
    setValue(valueCopy);
  };

  return (
    <div className="App">
    <FormControl component="fieldset">
    <FormLabel component="legend">Gender</FormLabel>
      <RadioGroup aria-label="gender" name="gender1" value="gender" value={value[0]} onChange={handleChange(0)}>
        <FormControlLabel value={value[0]} control={<Radio />} label={
          <Input type="text" value={value[0]} onChange={handleChange(0)}/>
        } />
        <FormControlLabel value={value[1]} control={<Radio />} label={
          <Input type="text" value={value[1]} onChange={handleChange(1)}/>
        } />
      </RadioGroup>
    </FormControl>
    <Button variant="contained" color="primary">
    Submit
    </Button>
    </div>
  );
}

export default App;
