import React from 'react';
import logo from './logo.svg';
import './App.css';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Container from '@material-ui/core/Container';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Input from '@material-ui/core/Input';
import Grid from '@material-ui/core/Grid';
import RadioButtonUncheckedIcon from '@material-ui/icons/RadioButtonUnchecked';

const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
    },
  },
}));

function App() {
  const [value, setValue] = React.useState(['Sample']);
  const [selected, setSelected] = React.useState('');
  const [question, setQuestion] = React.useState('Question?');

  const handleChange = (index) => (event) => {
    let valueCopy = [...value]
    valueCopy[index] = event.target.value;
    setSelected(event.target.value);
    setValue(valueCopy);
  };

  const handleRadioChange = (event) => {
    setSelected(event.target.value);
  };

  const handlePromptChange = (event) => {
    setQuestion(event.target.value);
  };

  const handleAddClick = (event) => {
    let valueCopy = [...value];
    valueCopy.push("Sample");
    setValue(valueCopy);
  };

  const handleRemoveClick = (event) => {
    let valueCopy = [...value];
    valueCopy.pop();
    setValue(valueCopy);
  };

  const classes = useStyles();

  return (
    <div className="App">
    <div className={classes.root}>
    <form className={classes.root} noValidate autoComplete="off">
    <TextField id="prompt" label={question} onChange={handlePromptChange}/>
    {value.map((item,index) =>
      <Grid container spacing={1} alignItems="flex-end">
        <Grid item>
          <RadioButtonUncheckedIcon />
        </Grid>
        <Grid item>
          <TextField id={"option-"+index} label={item} />
        </Grid>
      </Grid>
    )}
    </form>
    </div>
    <div className={classes.root}>
    <Button variant="contained" color="primary" onClick={handleAddClick}>
    Add
    </Button>
    <Button variant="contained" color="primary" onClick={handleRemoveClick}>
    Remove
    </Button>
    <Button variant="contained" color="primary">
    Save
    </Button>
    </div>
    </div>
  );
}

export default App;
