import React from 'react';
import logo from './logo.svg';
import './App.css';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Container from '@material-ui/core/Container';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Input from '@material-ui/core/Input';

function App() {
  const [value, setValue] = React.useState(['female','test']);
  const [selected, setSelected] = React.useState('female');
  const [question, setQuestion] = React.useState('Question?');

  const handleChange = (index) => (event) => {
    let valueCopy = [...value]
    valueCopy[index] = event.target.value;
    setSelected(event.target.value);
    setValue(valueCopy);
  };

  const handleRadioChange = (event) => {
    setSelected(event.target.value);
  };

  const handlePromptChange = (event) => {
    setQuestion(event.target.value);
  };

  return (
    <div className="App">
    <FormControl component="fieldset">
    <FormLabel component="legend">{<Input type="text" value={question} onChange={handlePromptChange}/>}</FormLabel>

        <FormControlLabel value={value[0]} control={<Radio />} label={
          <Input type="text" value={value[0]} onChange={handleChange(0)}/>
        } />
        <FormControlLabel value={value[1]} control={<Radio />} label={
          <Input type="text" value={value[1]} onChange={handleChange(1)}/>
        } />

    </FormControl>
    <Button variant="contained" color="primary">
    Submit
    </Button>
    </div>
  );
}

export default App;
